import {memo, useEffect, useState} from 'react';
import { connect } from 'react-redux';

import {getComments} from '../../redux/actions';

const Posts = memo((props) => {
  console.log(props);
  useEffect(()=>{
    props.getComments();
    console.log('useEffect executed');
  },[])
  return(
    <>
      <h1>Posts</h1>
      <h1>{props.isFetching?'...Loading':props.data&& props.data.size}</h1>

    </>
  );
});

Posts.getInitialProps = async function(ctx) {
  console.log('---ctx---', ctx.isServer);
  if(ctx.isServer){
      await ctx.store.dispatch(getComments());
  }
};

const mapStateToProps = (state) =>({
  comments:state.comments
})

export default connect(mapStateToProps,{getComments})(Posts);
