import {memo, useEffect, useState} from 'react';
import { connect } from 'react-redux';
import {getPosts} from '../redux/actions';

const Index = memo((props) => {
  console.log('-----props------',props);
  // useEffect(()=>{
  //   props.getPosts();
  // },[])
  return(<h1>{props.isFetching?'...Loading':props.data&& props.data.size}</h1>);
});

Index.getInitialProps = async function(ctx) {
  if(ctx.isServer){
      let res = await fetch('https://api.github.com/repos/zeit/next.js');
      res = await res.json();
      ctx.store.dispatch({type:`get_posts_success`, payload: res});
  }
};

const mapStateToProps = (state) =>({
  post:state.post
})

export default connect(state=>state.posts,{getPosts})(Index);
