import Router, { useRouter }  from 'next/router';

export const Comments = ({comments}) =>{
  const {query} = useRouter();
  return(
    <h3>
      {comments.map((data, i)=>(
        <h2 onClick={()=>Router.push(`posts/${query.pid}/comments/${i+1}`, 'posts/postId/comments/commentId')}>
          {data}
        </h2>))}
    </h3>
  )
}

Comments.getInitialProps = () =>{
  return {comments:['Hi','Hello','How r you?']};
}

export default Comments;
